import numpy
inp = ''
#print(inp[0:4])
strLen = 13
print(len(inp) + strLen)
productDict = {}

largestProd = 1

def multiplyList(myList) :
    # Multiply elements one by one
    result = 1
    for x in myList:
         result = result * x 
    return result

def getProduct(string): 
    lst=[] 
    lst[:0]=string
    intList = [int(i) for i in lst]
    res = multiplyList(intList)
    return res

for i in range(1,len(inp) * strLen*2):
    sub = inp[i:i+strLen]
    if len(sub) == strLen:
        product = getProduct(sub)
        #print(getProduct(sub))
        #productDict[sub] = str(product) +'('+str(len(product))+')'
        if product > largestProd:
           largestProd = product
           print('sub:'+sub+', product: '+str(largestProd) +', len:' +str(len(sub)))
print()
print(largestProd)
print('------------Product Dict------------')
for key, value in productDict.items():
    print(key, ' : ', value)

